/*
给定两个字符串形式的非负整数 num1 和num2 ，计算它们的和。

注意：

num1 和num2 的长度都小于 5100.
num1 和num2 都只包含数字 0-9.
num1 和num2 都不包含任何前导零。
你不能使用任何內建 BigInteger 库， 也不能直接将输入的字符串转换为整数形式。

来源：力扣（LeetCode）
链接：https://leetcode-cn.com/problems/add-strings
著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
*/

/*
因不能直接转换成整数，所以计划使用ASCII码进行计算
	1. 遍历两个字符串，从最后一位往前加
	2. ASCII的中数字的范围是48-57
	3.
*/
func addString2(num1 string, num2 string) string {
	len1 := len(num1)
	len2 := len(num2)

	max := len2
	if len1 > len2 {

		max = len1
	}

	str1, str2 := make([]uint8, max), make([]uint8, max)

	for k := range str1 {
		str1[k] = 48
		str2[k] = 48
	}

	for k := range num1 {
		str1[max-k-1] = num1[len1-k-1]
	}

	for k := range num2 {
		str2[max-k-1] = num2[len2-k-1]
	}

	var key uint8
	for i := 1; i <= max; i++ {
		num := str1[max-i] + str2[max-i] - 48 + key
		str1[max-i] = num
		fmt.Println(str1[max-i])
		if num > 57 {
			str1[max-i] -= 10
			key = 1
		} else {
			key = 0
		}
	}
	var sum string
	if key == 1 {
		sum = "1"
	}
	for k := range str1 {
		sum += string(str1[k])
	}
	return sum
}

func addStrings3(num1 string, num2 string) string {
	var sum string

	len1 := len(num1)
	len2 := len(num2)

	max := len1
	if len2 > len1 {
		max = len2
	}

	var n1, n2 uint8
	var key uint8
	for i := 1; i <= max; i++ {
		n1 = 48
		n2 = 48
		if len1-i >= 0 {
			n1 = num1[len1-i]
		}

		if len2-i >= 0 {
			n2 = num2[len2-i]
		}
		u := n1 + n2 - 48 + key
		if u > 57 {
			u -= 10
			key = 1
		} else {
			key = 0
		}
		sum = string(u) + sum
	}
	if key == 1 {
		sum = "1" + sum
	}
	return sum
}

func addStrings(num1 string, num2 string) string {
	//指定num1是较长字符串
	ln1 := len(num1)
	ln2 := len(num2)
	if ln1 < ln2 {
		t := num1
		num1 = num2
		num2 = t

		tn := ln1
		ln1 = ln2
		ln2 = tn
	}
	//在num1的基础上预留一位
	ans := make([]byte, ln1+1)
	//进位 默认值 byte(0)
	var ten byte
	//num1 num2倒叙遍历
	for i := ln1 - 1; i >= 0; i-- {
		var a0 byte
		//num2 对应index
		j := i - (ln1 - ln2)
		if j >= 0 {
			ten, a0 = byteAdd(num1[i]+ten, num2[j])
		} else {
			//num2 byte ='0' 补足
			ten, a0 = byteAdd(num1[i]+ten, '0')
		}
		ans[i+1] = a0
	}
	//最高位进位值修剪
	if ten > 0 {
		ans[0] = '1'
		return string(ans)
	} else {
		//修剪多余的0
		return string(ans[1:])
	}
}

//两个byte相加
//返回值 进位值 int (1 or 0)
//acsii byte
func byteAdd(a, b byte) (byte, byte) {
	ans := a + b - '0' - '0'
	if ans >= 10 {
		return 1, ans + '0' - 10
	}
	return 0, ans + '0'
}

func Test_T415(t *testing.T) {
	fmt.Println(addStrings("", ""))
}
